package com.whilerain.animationpractices.lesson1;import android.graphics.drawable.Animatable;import android.graphics.drawable.Drawable;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import android.view.View;import android.widget.ImageView;import com.whilerain.animationpractices.R;public class Lesson1FrameAnimActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_lesson1_frame_anim);        ImageView img = findViewById(R.id.img);        img.setBackgroundResource(R.drawable.anim_attack);        img.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Drawable attackAnimation = v.getBackground();                if (attackAnimation instanceof Animatable) {                    ((Animatable) attackAnimation).start();                }            }        });    }}