package com.whilerain.animationpractices.lesson1;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.LinearLayout;import com.whilerain.animationpractices.R;public class Lesson1LayoutAimationActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_lesson1_layout_aimation);        final LinearLayout list = findViewById(R.id.list);        final Button start = findViewById(R.id.start);        start.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                list.startLayoutAnimation();            }        });    }}