package com.whilerain.animationpractices.lesson2;import android.support.annotation.NonNull;import android.support.v4.view.ViewPager;import android.view.View;import android.widget.ImageView;import android.widget.TextView;import com.whilerain.animationpractices.R;public class MyPageTransormer implements ViewPager.PageTransformer {    @Override    public void transformPage(@NonNull View page, float position) {        int pageWidth = page.getWidth();        TextView title = page.findViewById(R.id.title);        TextView desc = page.findViewById(R.id.desc);        title.setTranslationX(position * 1.2f * pageWidth);        desc.setTranslationX(position * 1.7f * pageWidth);        if(page.getTag().equals("Intro1Fragment")){            ImageView image1 = page.findViewById(R.id.image1);            ImageView image2 = page.findViewById(R.id.image2);            image1.setTranslationX(-position * 0.7f * pageWidth);            image2.setTranslationX(-position * 0.3f * pageWidth);        }        else if(page.getTag().equals("Intro2Fragment")){            ImageView image1 = page.findViewById(R.id.image1);            ImageView image2 = page.findViewById(R.id.image2);            ImageView image3 = page.findViewById(R.id.image3);            image1.setTranslationX(-position * 0.8f * pageWidth);            image2.setTranslationX(-position * 0.8f * pageWidth);            image2.setRotation(Math.abs(-position * 180));            image3.setTranslationX(-position * 0.8f * pageWidth);            image3.setRotation( -position * 540);        }        else if(page.getTag().equals("Intro3Fragment")){            ImageView image1 = page.findViewById(R.id.image1);            ImageView image2 = page.findViewById(R.id.image2);            image1.setTranslationX(-position * 0.8f * pageWidth);            image2.setTranslationX(-position * 0.5f * pageWidth);            image2.setRotation(-position * 70);        }        else if(page.getTag().equals("Intro4Fragment")){            ImageView image1 = page.findViewById(R.id.image1);            ImageView image2 = page.findViewById(R.id.image2);            image1.setTranslationY(-position * 0.5f * page.getHeight());            image2.setTranslationY(position * 0.3f * page.getHeight());        }        else if(page.getTag().equals("GrantGpsFragment")){            ImageView image1 = page.findViewById(R.id.image1);            ImageView image2 = page.findViewById(R.id.image2);            image1.setTranslationX(-position * 0.8f * pageWidth);            image2.setTranslationX(-position * 0.8f * pageWidth);            image2.setRotation(Math.abs(-position * 360));        }    }}