package com.whilerain.animationpractices.lesson4;import android.graphics.drawable.AnimatedVectorDrawable;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import android.view.View;import android.widget.ImageView;import com.whilerain.animationpractices.R;public class Lesson4Activity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_lesson4);        final ImageView imageView = findViewById(R.id.image);        imageView.setImageDrawable(getDrawable(R.drawable.bluetooth_anim));        imageView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if (imageView.getDrawable() instanceof AnimatedVectorDrawable) {                    ((AnimatedVectorDrawable)imageView.getDrawable()).start();                }            }        });    }}