package com.whilerain.animationpractices.lesson5;import android.os.Bundle;import androidx.constraintlayout.widget.ConstraintLayout;import androidx.constraintlayout.widget.ConstraintSet;import androidx.appcompat.app.AppCompatActivity;import android.transition.TransitionManager;import android.view.View;import com.whilerain.animationpractices.R;public class Lesson5Activity extends AppCompatActivity {    private ConstraintLayout vConstraintLayout;    private ConstraintSet constraintSet1 = new ConstraintSet();    private ConstraintSet constraintSet2 = new ConstraintSet();    private Boolean showList = false;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_lesson5);        vConstraintLayout = findViewById(R.id.vConstraintLayout);        constraintSet1.clone(vConstraintLayout);        constraintSet2.clone(this, R.layout.activity_lesson5_1);        findViewById(R.id.vKoso).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if (!showList) {                    TransitionManager.beginDelayedTransition(vConstraintLayout);                    constraintSet2.applyTo(vConstraintLayout);                    showList = true;                }            }        });    }    @Override    public void onBackPressed() {        if(showList){            TransitionManager.beginDelayedTransition(vConstraintLayout);            constraintSet1.applyTo(vConstraintLayout);            showList = false;        }else {            super.onBackPressed();        }    }}