package com.whilerain.animationpractices.lesson6;import android.app.ActivityOptions;import android.content.Intent;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import android.util.Pair;import android.view.View;import android.widget.Button;import android.widget.LinearLayout;import android.widget.TextView;import com.whilerain.animationpractices.R;public class Lesson6ShareElementsActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_lesson6_share_elements);        Button go = findViewById(R.id.go);        final TextView title = findViewById(R.id.title);        final LinearLayout root = findViewById(R.id.root);        go.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(Lesson6ShareElementsActivity.this, Lesson6ShareElement1Activity.class);                ActivityOptions options = ActivityOptions                        .makeSceneTransitionAnimation(Lesson6ShareElementsActivity.this, new Pair(root, "root"), new Pair(title, "title"));                startActivity(intent, options.toBundle());            }        });    }}